<template>
  <IonPage>
    <!-- Header / Navbar -->
    <IonHeader class="header">
      <IonToolbar class="navbar">
        <ul>
          <li>
            <router-link class="nav-link" to="/app/contenido">
              <img src="@/assets/icons8-página-principal-60.png" alt="Inicio" class="icon" />
            </router-link>
          </li>
          <li>
            <router-link class="nav-link" to="#">
              <img src="@/assets/icons8-brújula-100.png" alt="Búsqueda" class="icon" />
            </router-link>
          </li>
          <li>
            <router-link class="nav-link" to="/app/chats">
              <img src="@/assets/icons8-burbuja-de-diálogo-100.png" alt="Chats" class="icon" />
            </router-link>
          </li>
          <li>
            <router-link class="nav-link" to="#">
              <img src="@/assets/icons8-más-100.png" alt="Agregar" class="icon" />
            </router-link>
          </li>
          <li>
            <router-link class="nav-link" to="/app/perfil">
              <img src="@/assets/icons8-perfil-de-gato-100.png" alt="Perfil" class="icon" />
            </router-link>
          </li>
        </ul>
      </IonToolbar>
    </IonHeader>

    <!-- Contenido dinámico -->
    <IonContent class="main">
      <router-view />
    </IonContent>

    <!-- Reproductor de música Lofi oculto -->
    <audio ref="lofiAudio" autoplay loop hidden>
      <source src="@/assets/lofi-background-music-326931.mp3" type="audio/mpeg" />
    </audio>
  </IonPage>
</template>

<script setup>
import { IonPage, IonHeader, IonToolbar, IonContent } from "@ionic/vue";
import { ref, onMounted } from "vue";

const lofiAudio = ref(null);

onMounted(() => {
  // Algunos navegadores bloquean autoplay sin interacción, este try ayuda
  if (lofiAudio.value) {
    const playMusic = async () => {
      try {
        await lofiAudio.value.play();
      } catch (e) {
        console.warn("Autoplay bloqueado, se reproducirá al interactuar:", e);
      }
    };
    playMusic();
  }
});
</script>

<style scoped>
/* Navbar fijo y tamaño */
:deep(.header),
:deep(.navbar) {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 50px;
  z-index: 1000;
  --background: rgba(15, 15, 15, 0.75) !important;
  --color: #fff;
  backdrop-filter: blur(12px);
  -webkit-backdrop-filter: blur(12px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.15);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0 1rem;
}

:deep(.navbar ul) {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 3rem;
  padding: 0;
  margin: 0;
  list-style: none;
  height: 100%;
}

:deep(.navbar ul li) {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
}

:deep(.nav-link) {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
  padding: 0;
}

:deep(.icon) {
  width: 28px;
  height: 28px;
  object-fit: contain;
  display: block;
}

:deep(.navbar ul::-webkit-scrollbar) {
  display: none;
}

@media (max-width: 600px) {
  :deep(.navbar ul) {
    gap: 1.5rem;
  }
  :deep(.icon) {
    width: 24px;
    height: 24px;
  }
}
</style>
