import { ref, computed, onMounted, onBeforeUnmount } from 'vue';
import '@/assets/anime.css'; // Importar estilos externos

export default {
  __name: 'AnimeView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const animes = ref([]);
    const showModal = ref(false);
    const selectedAnime = ref(null);
    const selectedAnimeImage = computed(() => {
      if (!selectedAnime.value) return '';
      return selectedAnime.value.images?.jpg?.large_image_url || selectedAnime.value.images?.jpg?.image_url || '';
    });
    const loadAnimes = async () => {
      try {
        // Datos simulados (puedes reemplazar por API)
        animes.value = [{
          "id": 5114,
          "title": "Fullmetal Alchemist: Brotherhood",
          "year": 2009,
          "episodes": 64,
          "type": "TV",
          "synopsis": "Dos hermanos alquimistas buscan la piedra filosofal...",
          "images": {
            "jpg": {
              "image_url": "https://cdn.myanimelist.net/images/anime/1208/94745.jpg"
            }
          }
        }, {
          "id": 30276,
          "title": "One Punch Man",
          "year": 2015,
          "episodes": 12,
          "type": "TV",
          "synopsis": "Saitama, un héroe tan fuerte que derrota a cualquiera...",
          "images": {
            "jpg": {
              "image_url": "https://cdn.myanimelist.net/images/anime/12/76049.jpg"
            }
          }
        }
        // ... (el resto de animes que ya tienes)
        ];
      } catch (err) {
        console.error("Error cargando animes:", err);
      }
    };
    const showDetail = anime => {
      selectedAnime.value = anime;
      showModal.value = true;
    };
    const closeModal = () => {
      showModal.value = false;
      selectedAnime.value = null;
    };
    const handleClickOutside = event => {
      if (event.target === document.getElementById('animeModal')) {
        closeModal();
      }
    };
    onMounted(() => {
      loadAnimes();
      window.addEventListener('click', handleClickOutside);
    });
    onBeforeUnmount(() => {
      window.removeEventListener('click', handleClickOutside);
    });
    const __returned__ = {
      animes,
      showModal,
      selectedAnime,
      selectedAnimeImage,
      loadAnimes,
      showDetail,
      closeModal,
      handleClickOutside,
      ref,
      computed,
      onMounted,
      onBeforeUnmount
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
