<template>
  <IonPage>
    <IonHeader>
      <IonToolbar>
        <IonTitle>Perfil</IonTitle>
      </IonToolbar>
    </IonHeader>

    <IonContent class="ion-padding">
      <IonCard>
        <!-- Imagen de fondo -->
        <IonImg src="" alt="Fondo del perfil" />

        <IonCardContent>
          <!-- Acorde√≥n Gustos -->
          <IonAccordionGroup :value="activeAccordion">
            <IonAccordion value="gustos">
              <IonItem slot="header" button @click="toggleAccordion('gustos')">
                <IonLabel>Gustos</IonLabel>
                <IonIcon :icon="chevronDown" slot="end" />
              </IonItem>
              <div class="ion-padding" slot="content">
                <ul>
                  <li v-for="(gusto, index) in profileData.gustos" :key="index">
                    {{ gusto }}
                  </li>
                </ul>
              </div>
            </IonAccordion>

            <!-- Acorde√≥n Logros -->
            <IonAccordion value="logros">
              <IonItem v-slot="header" button @click="toggleAccordion('logros')">
                <IonLabel>Logros</IonLabel>
                <IonIcon :icon="chevronDown" slot="end" />
              </IonItem>
              <div class="ion-padding" slot="content">
                <ul>
                  <li v-for="(logro, index) in profileData.logros" :key="index">
                    {{ logro }}
                  </li>
                </ul>
              </div>
            </IonAccordion>
          </IonAccordionGroup>

          <!-- Avatar y usuario -->
          <div class="user-info-section">
            <div class="user-avatar-container" @click="changeAvatar">
              <IonAvatar>
                <IonImg :src="profileData.avatar_url" alt="Avatar del usuario" />
              </IonAvatar>
              <input
                type="file"
                ref="avatarInput"
                style="display: none"
                @change="handleFileChange"
                accept="image/*"
              />
            </div>
            <IonLabel class="username">{{ profileData.username }}</IonLabel>
          </div>
        </IonCardContent>
      </IonCard>
    </IonContent>
  </IonPage>
</template>

<script setup>
import { ref, onMounted } from "vue";
import { IonPage, IonHeader, IonToolbar, IonTitle, IonContent, IonCard, IonCardContent, IonItem, IonLabel, IonIcon, IonAccordion, IonAccordionGroup, IonImg, IonAvatar } from "@ionic/vue";
import { chevronDown } from "ionicons/icons";
import { createClient } from "@supabase/supabase-js";

// üëâ Supabase Client
const supabase = createClient(
  "https://neffqwnqpcydidpkysfi.supabase.co",
  "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..."
);

// Estado del acorde√≥n
const activeAccordion = ref(null);

// Perfil
const profileData = ref({
  username: "",
  avatar_url: "",
  gustos: [],
  logros: [],
});

const avatarInput = ref(null);

// üëâ Alternar acorde√≥n
const toggleAccordion = (panelName) => {
  activeAccordion.value = activeAccordion.value === panelName ? null : panelName;
};

// üëâ Cambiar avatar
const changeAvatar = () => {
  avatarInput.value.click();
};

const handleFileChange = async (event) => {
  const file = event.target.files[0];
  if (!file) return;

  try {
    const filePath = `avatars/${profileData.value.username}/${Date.now()}.${file.name.split(".").pop()}`;

    const { data, error } = await supabase.storage
      .from("avatars")
      .upload(filePath, file);

    if (error) throw error;

    const publicUrl = `https://neffqwnqpcydidpkysfi.supabase.co/storage/v1/object/public/${data.path}`;

    const { error: updateError } = await supabase
      .from("profiles")
      .update({ avatar_url: publicUrl })
      .eq("username", profileData.value.username);

    if (updateError) throw updateError;

    profileData.value.avatar_url = publicUrl;
    alert("‚úÖ Imagen de perfil actualizada correctamente!");
  } catch (error) {
    console.error("Error al subir la imagen:", error.message);
    alert("‚ùå Error al subir la imagen. Int√©ntalo de nuevo.");
  }
};

// üëâ Cargar datos del perfil
const fetchProfileData = async () => {
  try {
    const usuarioActual = JSON.parse(localStorage.getItem("usuario"));
    if (!usuarioActual) {
      console.warn("No hay usuario autenticado.");
      return;
    }

    const { data, error } = await supabase
      .from("perfiles")
      .select("nombre_perfil, avatar_url, gustos, logros")
      .eq("nombre_perfil", usuarioActual.nombre)
      .single();

    if (error) throw error;

    if (data) {
      profileData.value.username = data.nombre_perfil;
      profileData.value.avatar_url = data.avatar_url;
      profileData.value.gustos = data.gustos || [];
      profileData.value.logros = data.logros || [];
    }
  } catch (error) {
    console.error("Error al cargar datos del perfil:", error.message);
  }
};

onMounted(() => {
  fetchProfileData();
});
</script>

