import perfilImage from '@/assets/perfil.jpeg';
// Importación del CSS desde components
import '@/components/portfolio.css';
import { ref } from 'vue';

// Estado reactivo

export default {
  __name: 'PortafolioView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    // Importación de la imagen desde assets
    const showDetail = ref(false);
    const imagePath = perfilImage;
    const portfolioData = {
      nombre: "Anthony Ospina Fernández",
      carrera: "Ingeniería en Sistemas",
      especializacion: "Desarrollador Full-Stack"
    };

    // Método para mostrar la card
    const loadData = () => {
      showDetail.value = true;
    };
    const __returned__ = {
      showDetail,
      imagePath,
      portfolioData,
      loadData,
      get perfilImage() {
        return perfilImage;
      },
      ref
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
