import { createRouter } from "@ionic/vue-router"
import { Capacitor } from "@capacitor/core"
import MainLayout from "@/layouts/MainLayout.vue"
import LoginView from "@/views/LoginView.vue"
import { createWebHistory, createWebHashHistory } from "vue-router"

// Si corre en Capacitor (APK/móvil) usa hash, si no usa history normal
const history = Capacitor.isNativePlatform()
  ? createWebHashHistory()
  : createWebHistory()

const routes = [
  {
    path: "/login",
    component: LoginView
  },
  {
    path: "/app",
    component: MainLayout,
    children: [
      { path: "contenido", component: () => import("@/views/ContenidoView.vue") },
      { path: "perfil", component: () => import("@/views/PerfilView.vue") },
      { path: "chats", component: () => import("@/views/ChatsView.vue") }
    ]
  },
  { path: "/", redirect: "/app/contenido" }
]

export default createRouter({
  history,
  routes
})
